                    style={{
                      position: 'absolute',
                      left: '50%',
                      top: '50%',
                      transform: `translate(-50%, -50%) rotateY(${i * 60}deg) translateZ(150px)`,
                      width: '60px',
                      height: '60px',
                      borderRadius: '15px',
                      background: 'rgba(15, 23, 42, 0.8)',
