                  const x2_o = 500 + outerR * Math.cos(radEnd);
                  const y2_o = 500 + outerR * Math.sin(radEnd);

                  const midRad = (angle + sliceAngle / 2 - 90) * (Math.PI / 180);
                  const textR = 310;
                  const textX = 500 + textR * Math.cos(midRad);
                  const textY = 500 + textR * Math.sin(midRad);

