                        d={`M ${x1_i} ${y1_i} L ${x1_o} ${y1_o} A ${outerR} ${outerR} 0 0 1 ${x2_o} ${y2_o} L ${x2_i} ${y2_i} A ${innerR} ${innerR} 0 0 0 ${x1_i} ${y1_i}`}
                        fill={item.color}
                        fillOpacity={isHovered ? 0.95 : 0.8}
                        stroke={isHovered ? "white" : "rgba(2, 6, 23, 0.4)"}
                        strokeWidth={isHovered ? 4 : 1}
                        animate={{ 
