                      />
                      <text
                        x={textX} y={textY}
                        fill="white"
                        fontSize="12"
                        fontWeight="950"
                        textAnchor="middle"
                        style={{ 
                          pointerEvents: 'none',
                          transform: `rotate(${angle + sliceAngle/2 > 90 && angle + sliceAngle/2 < 270 ? angle + sliceAngle/2 + 180 : angle + sliceAngle/2}deg)`,
                          transformOrigin: `${textX}px ${textY}px`,
                          textShadow: '0 2px 8px rgba(0,0,0,0.9)',
                          letterSpacing: '1px'
                        }}
                      >
                        {item.name.toUpperCase()}
                      </text>
                    </g>
                  );
                })}
