                   style={{ 
                     padding: '3rem', 
                     borderRadius: '40px',
                     minHeight: '550px',
                     display: 'flex',
                     flexDirection: 'column',
                     justifyContent: 'center',
                     border: `2px solid ${hoveredItem.color}`,
                     background: `linear-gradient(135deg, ${hoveredItem.color}20 0%, rgba(10,15,30,0.98) 100%)`,
