import { Hammer, Brain, FolderOpen, CheckCircle2, XCircle, Layers, FileCode, Folder, Play, Trophy, Sparkles, Code2, ShieldCheck, Zap, Monitor } from 'lucide-react';

// --- CLEANED GAME CONTENT ---
const masterLevels = [
